        public IActionResult Index(int id =-1)
        {
            ViewData["Articles"] = Articles;
            ViewData["authors"] = authors;
            ViewData["id"] = id;

            return View("Index");
        }

        public IActionResult Details(int? id)
        {
            ArticlesModel blog1 = Articles.Find(b => b.Article_id == id);

            if (blog1 == null)
            {
                return Content("Not found");
            }
            else
            {
                ViewData["Articles"] = Articles;
                ViewData["authors"] = authors;
                ViewData["id"] = id;


                return View("Details");
            }
